const Feedback = (props) => {
    const [show, setShow] = useState(props.feedback.open);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);

    const handleClick = (event) => {
      setShow(!show);
      setTarget(event.target);
    };

    return (
      <div ref={ref}>  
        <Overlay
          show={show}
          target={target}
          placement="bottom"
          container={ref.current}
          containerPadding={20}
        >
          <Alert 
            variant={props.feedback.type}
            onClose={handleClick}
          >
            {props.feedback.message}
          </Alert>
        </Overlay>
      </div>
    );
  };

  export default Feedback;
